import React, { useState } from "react"
import './MovieApp.css';
import { AiOutlineSearch } from "react-icons/ai";
import axios from 'axios';

export default function MovieApp(){

    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('popularity.desc');
    const [genres, setGenres] =useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');

    useEffect(() => {
        const fetchGenres = async () => {
            const response = await axios.get(
               'https://www.omdbapi.com/apikey.aspx/list', 
               {
                 params:{
                    api_key:'9c72c845',
                 },
               }
            );
            setGenres(response.data.genres);
            console.log(response.data.genres);
        };
        fetchGenres();
    },[]);

    useEffect(() => {
        const fetchMovies = async () => {
            const response = await axios.get(
                'https://www.omdbapi.com/apikey.aspx/discover/movies',
                {
                    params: {
                        api_key:'9c72c845',
                        sort_by: sortBy,
                        page: 1,
                        with_geners: selectedGenre,
                        query: searchQuery,
                    },
                }
            );
            setMovies(response.data.results);
        };
        fetchMovies();
    }, [searchQuery, sortBy, selectedGenre]);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    }
    const handleSearchSubmit = async()=> {
        const response = await axios.get(
            'https://www.omdbapi.com/apikey.aspx',
            {
                params:{
                    api_key:'9c72c845',
                    query: searchQuery,
                },
            }

        );
        setMovies(response.data.results);
        
    }   
    const handleSortChange = (event) => {
        setSortBy(event.target.value);
    }
    
    const handleGenreChange = (event) => {
        setSelectedGenre(event.target.value);
    }

    return(
        <div>
            <h1>MovieHouse</h1>
            <div className='search-bar'>
                <input type='text' placeholder='Search Movies...' value={searchQuery} onChange={handleSearchChange} className='search-input'></input>
                <button onClick={handleSearchSubmit} className='search-button'>
                  <AiOutlineSearch />
                </button>
                
            </div>
            <div className='filters'>
                <label htmlFor='sort-by'>Sort By:</label>
                <select id='sort-by' value={sortBy} onChange={handleSortChange}>
                <option value="popularity.desc">Popularity Descending</option>
                <option value="popularity.asc">Popularity Ascending</option>
                <option value="vote_average.desc">Rating Descending</option>
                <option value="vote_average.asc">Rating Ascending</option>
                <option value="release_date.desc">Release Date Descending</option>
                <option value="release_date.asc">Release Date Ascending</option>
                </select>
                <label htmlFor='genre'>Genre:</label>

                <select id='genre' value={selectedGenre} onChange={handleGenreChange}>
                <option value="">All Genres</option>
                {genres.map((genre)=>{
                    <option key={genre.id} value={genre.id}>{genre.name}</option>
                })}
                </select>          
            </div>
        </div>
    )
}
