import React, { useState, useEffect } from "react"
import './MovieApp.css';
import { AiOutlineSearch } from "react-icons/ai";
import axios from 'axios';



export default function MovieApp(){

    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('popularity.desc');
    const [genres, setGenres] =useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [expandedMovieId, setExpandedMovieId] = useState(null);

    useEffect(() => {
        const fetchGenres = async () => {
            const response = await axios.get(
               'http://www.omdbapi.com/', 
               {
                 params:{
                    apikey:'9c72c845',
                    s: searchQuery,
                 },
               }
            );
            setGenres(response.data.genres);
            console.log(response.data.genres);
        };
        fetchGenres();
    },[]);

    useEffect(() => {
        const fetchMovies = async () => {
            const response = await axios.get(
                'http://www.omdbapi.com/',
                {
                    params: {
                        apikey:'9c72c845',
                        i: movieId,
                        sort_by: sortBy,
                        page: 1,
                        with_genres: selectedGenre,
                        query: searchQuery,
                    },
                }
            );
            setMovies(response.data.results);
        };
        fetchMovies();
    }, [searchQuery, sortBy, selectedGenre]);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleSortChange = (event) => {
        setSortBy(event.target.value);
    };
    
    const handleGenreChange = (event) => {
        setSelectedGenre(event.target.value);
    };

    const handleSearchSubmit = async()=> {
        try {
        const response = await axios.get(
            'http://www.omdbapi.com/',
            {
                params:{
                    apikey:'9c72c845',
                    s: searchQuery,
                },
            }

        );
        console.log(response.data.Search);
        setMovies(response.data.Search || []);
    }
    catch (error) {
        console.error('Error fetching movies', error);
      }
    }; 
    
    const toggleDescription = (movieId) => {
        setExpandedMovieId(expandedMovieId === movieId ? null : movieId);
    };

    return(
        <div>
            <h1>MovieHouse</h1>
            <div className='search-bar'>
                <input type='text' placeholder='Search Movies...' value={searchQuery} onChange={handleSearchChange} className='search-input'></input>
                <button onClick={handleSearchSubmit} className='search-button'>
                  <AiOutlineSearch />
                </button>
                
            </div>
            <div className='filters'>
                <label htmlFor='sort-by'>Sort By:</label>
                <select id='sort-by' value={sortBy} onChange={handleSortChange}>
                <option value="popularity.desc">Popularity Descending</option>
                <option value="popularity.asc">Popularity Ascending</option>
                <option value="vote_average.desc">Rating Descending</option>
                <option value="vote_average.asc">Rating Ascending</option>
                <option value="release_date.desc">Release Date Descending</option>
                <option value="release_date.asc">Release Date Ascending</option>
                </select>
                <label htmlFor='genre'>Genre:</label>

                <select id='genre' value={selectedGenre} onChange={handleGenreChange}>
                <option value="">All Genres</option>
                {genres.map((genre)=>{
                    <option key={genre.id} value={genre.id}>{genre.name}</option>
                })}
                </select>          
            </div>
            <div className="movie-wrapper">
                {movies.map((movie)=> (
                    <div key={movie.id} className="movie">
                        <ing src={'https://image.tmdb.org/t/p/w500${movie.poster_path}'} alt={movie.title} />
                        <h2>{movie.title}</h2>
                        <p className='rating'>Rating: {movie.vote_average}</p>
                        {expandedMovieId === movie.id ? (
                            <p>{movie.overview}</p>
                        ) : (
                            <p>{movie.overview.substring(0, 150)}...</p>
                        )}
                        <button onClick={() =>
                            toggleDescription(movie.id)}
                            className='read-more'>
                            {expandedMovieId === movie.id ? 'Show Less' : 'Read More'}
                            </button>
                        
                        </div>
                ))
                }
            </div>
        </div>
    )
}
