import React, { useState } from "react";
import './MovieApp.css';
import { AiOutlineSearch } from "react-icons/ai";
import axios from 'axios';

export default function MovieApp() {
  const [movies, setMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [expandedMovieId, setExpandedMovieId] = useState(null);
  const [movieDescriptions, setMovieDescriptions] = useState({});

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = async () => {
    try {
      const response = await axios.get('https://www.omdbapi.com/', {
        params: {
          apikey: '9c72c845',
          s: searchQuery,
        },
      });
      console.log(response.data);

      if (response.data.Response === "True") {
        setMovies(response.data.Search);
      } else {
        setMovies([]);
        alert(response.data.Error);
      }

    } catch (error) {
      console.error('Error fetching movies', error);
    }
  };

  const toggleDescription = async (movieId) => {
    if (expandedMovieId === movieId) {
      setExpandedMovieId(null);
    } else {
      // If description not fetched yet, fetch it
      if (!movieDescriptions[movieId]) {
        try {
          const response = await axios.get('https://www.omdbapi.com/', {
            params: {
              apikey: '9c72c845',
              i: movieId,
            },
          });
          setMovieDescriptions(prev => ({
            ...prev,
            [movieId]: response.data.Plot,
          }));
        } catch (error) {
          console.error('Error fetching movie description', error);
        }
      }
      setExpandedMovieId(movieId);
    }
  };

  return (
    <div>
      <h1>MovieHouse</h1>
      <div className='search-bar'>
        <input
          type='text'
          placeholder='Search Movies...'
          value={searchQuery}
          onChange={handleSearchChange}
          className='search-input'
        />
        <button onClick={handleSearchSubmit} className='search-button'>
          <AiOutlineSearch />
        </button>
      </div>

      <div className="movie-wrapper">
        {movies.map((movie) => (
          <div key={movie.imdbID} className="movie">
            <img
              src={movie.Poster !== "N/A" ? movie.Poster : "https://via.placeholder.com/400"}
              alt={movie.Title}
            />
            <h2>{movie.Title}</h2>
            <p className='rating'>Year: {movie.Year}</p>

            {expandedMovieId === movie.imdbID && (
              <p>{movieDescriptions[movie.imdbID] || "Loading description..."}</p>
            )}

            <button
              onClick={() => toggleDescription(movie.imdbID)}
              className='read-more'
            >
              {expandedMovieId === movie.imdbID ? 'Show Less' : 'Read More'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
